// Generated by CoffeeScript 1.6.3
$(function() {
  var format_pbx, search_response_container;
  format_pbx = function(pbx) {
    var display_location, response;
    display_location = pbx.location === "home" ? "vs." : "@";
    response = "<li class='query-response'>";
    response += "<a class='bsaketball-reference-link' href='http://www.basketball-reference.com/boxscores/" + pbx.basketball_reference_id + ".html' target='blank'>";
    response += "" + pbx.player + " " + pbx.points + " points " + display_location + " " + (pbx.opponent.toUpperCase()) + " on " + pbx.gamedate;
    response += "</a>";
    response += "</li>";
    return response;
  };
  search_response_container = $(".search-response-container ul");
  return $(".search-form").on("submit", function(e) {
    var query;
    e.preventDefault();
    search_response_container.html("");
    query = $(this).find(".search-query").val();
    return $.ajax({
      method: "GET",
      url: "/query?q=" + query,
      success: function(data) {
        var sorted_results;
        sorted_results = _.sortBy(data.search, function(pbx) {
          return pbx.points;
        });
        return _.each(sorted_results, function(pbx) {
          return search_response_container.append(format_pbx(pbx));
        });
      }
    });
  });
});
